package CodeImplementation;

public class DisplayCharacters 
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		
	    for(char c = 'A'; c <= 'Z'; ++c)
	      System.out.print(c + " ");
	      System.out.print("\n");
	    for(char c = 'a'; c <= 'z'; ++c)
		  System.out.print(c + " ");
		}

}
